function params = displayParams% For MEG LCD projector, Jun 25, 2014% Last calibrated: Using Denis Pelli's Minolta photomoter, held by hand% lying MEG, while executing CalibrateMonitorPhotometer in PTB (33% values, two measurements for each point)%%% % CODE TO SAVE GAMMA TABLE% % load in a measurement made from PTB's CalibrateMonitorPhotometer% load('/Volumes/server/Projects/MEG/Calibration/MEG_Cal1_33points_v1.mat')% % % use gammaTable 1, which is the powerlaw fit to the measurements% g = gammaTable1;% % % replicate to 256 x 3 for RGB (assuming 8 bits)% gamma = [g g g];% % % integers for lookup table% gammaTable = round(gamma * 255);% % % where to save?% pth = '~/matlab/git/vistadisp/exptTools2/displays/meg_lcddisplayParams';% % save(fullfile(pth, 'gamma'), 'gamma', 'gammaTable');% Critical parametersparams.numPixels    = [1024 768];   % this is the correct native resolutionparams.dimensions   = [33.12 20.7]; % cm, BUT CHECK THIS!params.distance     = 60;           % cm, CHECK THIS!params.frameRate    = 60;           % this is the correct native frame rateparams.cmapDepth    = 8;            % this is the correct bit depth for our experimentparams.screenNumber = 0;            % we usually mirror MEG, so 0 is correct% Descriptive parametersparams.position = 'lying in MEG';params.stereoFlag = 0;